import safeJsonParse from './safeJsonParse';
import safeJsonStringify from './safeJsonStringify';

var jsonParseToExport;
var jsonStringifyToExport;

if (process.env.POUCHDB_LITE) {
  jsonParseToExport = JSON.parse;
  jsonStringifyToExport = JSON.stringify;
} else {
  jsonParseToExport = safeJsonParse;
  jsonStringifyToExport = safeJsonStringify;
}

export {
  jsonParseToExport as safeJsonParse,
  jsonStringifyToExport as safeJsonStringify
};
