var vuvuzela;
if (!process.env.LITE_MODE) {
  vuvuzela = require('vuvuzela');
}

function safeJsonStringify(json) {
  if (process.env.LITE_MODE) {
    return JSON.stringify(json);
  } else {
    try {
      return JSON.stringify(json);
    } catch (e) {
      /* istanbul ignore next */
      return vuvuzela.stringify(json);
    }
  }
}

export default safeJsonStringify;